import styled from 'styled-components'

export const LoaderContainer = styled.div`
    background-color: rgba(0, 0, 0, 0.1);
    position: absolute;
    height: 100vh;
    width: 100vw;
    top:0;
    left:0;
    display: flex;
    justify-content: center;
    align-items: center;
`;
