import React, { Component } from 'react'
import { connect }  from 'react-redux'
import { buscaVideo } from '../../Store/Actions/searchAC'

import logo from '../../assets/img/devaneios-code-logo.png'

import { HeaderContainer} from './styles'
import { LogoContainer } from './styles'
import { Busca } from './styles'
import { Listavideo } from './styles'
import { Item } from './styles'

class Header extends Component {

    buscaInput = e => {
        if(e.keyCode === 13){
            const valor = e.target.value            
            console.log(valor)
            this.props.buscaVideo(valor)
        }
        
    }
    render(){
        return(
            <>
                <HeaderContainer>
                    <LogoContainer>
                        <img src={logo} alt="Logo Devaneios Code" />
                    </LogoContainer>
                    <Busca>
                        <input type="text" placeholder="Busca"  
                        onKeyDown={(e) => this.buscaInput(e)}
                        />
                    </Busca>
                </HeaderContainer>
            </>
        )
    }
}

const mapDispatchToProps = (dispatch ) => {
    return{
        buscaVideo: ( params ) => dispatch (
            buscaVideo(params) 
        )
    }
}

class Lista extends Component {
    
    loadListVideos (video) {
        return(
            <Item>    
                <img src={video.snippet.thumbnails.default.url} alt="" />                    
                <div>
                    <h2>{video.snippet.title}</h2>
                </div>
            </Item>    
        )
    }


    render(){
        return(
            <>
                <Listavideo>
                    {
                        this.props.videos.map(video => {
                            return this.loadListVideos(video)
                        })
                    }          
                </Listavideo>
            </>
        )
    }
}

const mapStateToProps = (state => {
    return {
        videos: state.search.videos,
        loading: state.search.loading,
        error: state.search.error
    }
})

export default connect(null, mapDispatchToProps) (Header)
