import youtubeSearch from 'youtube-api-v3-search'

import YTApi from '../../api'

const API_KEY = YTApi

export const serchVideo = (params) =>{
    return dispatch =>{
            dispatch(searchVideoStart())
            youtubeSearch(API_KEY, {q: params})

            .then((data) => dispatch (searchVideoSucess(data.items)))
            .catch(() => dispatch (searchVideoError()))
    }

}

export const searchVideoStart = () =>{
    return{
        type: 'SEARCH_VIDEO',
        loading: true,
        error: false
    }
}

export const searchVideoSucess = (Videos) =>{
    return{
        type: 'SEARCH_VIDEO_SUCESS',
        videos,
        loading: false,
        error: false
    }
}

export const searchVideoError = () =>{
    return{
        type: 'SEARCH_VIDEO_ERROR',
        loading: false,
        error: true
    }
}
